Method.Implementation:
set st = $$$OK
#dim cmp As Competition = ""
if (..%ExistsId(data.id) = $$$YES){ set cmp = ..%OpenId(data.id,,.st) $$$THROWONERROR(st, st) } else{ set cmp = ..%New() set cmp.WDSFId = data.WDSFId }
set cmp.WDSFEventId = data.WDSFEventId set cmp.WDSFGroupId = data.WDSFGroupId
#dim trn As Tournament = ""
// Open | create tournament if (##class(Tournament).%ExistsId(data.WDSFGroupId) = $$$YES){ set trn = ##class(Tournament).%OpenId(data.WDSFGroupId,,.st) } else{ set trnData = ##class(%ZEN.proxyObject).%New() set trnData.WDSFId = data.WDSFGroupId set trn = ##class(Tournament).Save(trnData, .st) $$$THROWONERROR(st, st) }
set cmp.Tournament = trn set cmp.Country = ##class(Country).GetByName(data.country) set cmp.Discipline = ##class(Discipline).GetByName(data.discipline) set cmp.Division = ##class(Division).GetByName(data.division) set cmp.CStatus = ##class(CompetitionStatus).GetByName(data.status) set cmp.Type = ##class(CompetitionType).GetByName(data.type) set cmp.AgeGroup = ##class(AgeGroup).GetByName(data.ageGroup) set cmp.Location = data.location set cmp.Coefficient = data.coefficient
set cmp.StartDate = $ZDH(data.startDate, 3) set cmp.WDSFLastModified = data.lastModifiedDate set cmp.LastSyncWDSF = $ZDT($ZTS, 3)
set st = cmp.%Save()
quit cmp

set name = ""
&sql(SELECT Name INTO :name FROM WC_Data.Discipline WHERE ID = :disciplineId)	
if ($FIND($ZCVT(name, "U"), "SINGLE") > 0) || ($FIND($ZCVT(name, "U"), "SOLO") > 0) {
	quit $$$YES
}

quit $$$NO
